import React, { useState, useEffect } from 'react';
import { MessageSquare, Phone, QrCode, Wifi, WifiOff, RefreshCw, Trash2, Send, Users, MessageCircle, Monitor, Settings, Download, Upload, Eye, EyeOff, AlertTriangle, CheckCircle, Copy, Smartphone, Zap } from 'lucide-react';
import toast from 'react-hot-toast';

interface WhatsAppMessage {
  id: string;
  from: string;
  to: string;
  body: string;
  timestamp: number;
  fromMe: boolean;
  hasMedia?: boolean;
  type: string;
  contact?: {
    name: string;
    number: string;
  };
}

interface WhatsAppChat {
  id: string;
  name: string;
  isGroup: boolean;
  lastMessage?: {
    body: string;
    timestamp: number;
    fromMe: boolean;
  };
  unreadCount: number;
}

interface WhatsAppContact {
  id: string;
  name: string;
  number: string;
  isMyContact: boolean;
  profilePicUrl?: string;
}

export const WhatsAppQRManager: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'qr' | 'dashboard' | 'messages' | 'contacts' | 'settings'>('qr');
  const [isConnected, setIsConnected] = useState(false);
  const [qrCode, setQrCode] = useState<string | null>(null);
  const [connectionStep, setConnectionStep] = useState<'disconnected' | 'generating' | 'waiting' | 'connected'>('disconnected');
  const [messages, setMessages] = useState<WhatsAppMessage[]>([]);
  const [chats, setChats] = useState<WhatsAppChat[]>([]);
  const [contacts, setContacts] = useState<WhatsAppContact[]>([]);
  const [selectedChat, setSelectedChat] = useState<WhatsAppChat | null>(null);
  const [messageText, setMessageText] = useState('');
  const [stats, setStats] = useState({
    totalMessages: 0,
    totalChats: 0,
    totalContacts: 0,
    messagesReceived: 0
  });

  // Carregar dados salvos
  useEffect(() => {
    const savedConnection = localStorage.getItem('whatsapp_qr_connected');
    const savedMessages = localStorage.getItem('whatsapp_qr_messages');
    const savedChats = localStorage.getItem('whatsapp_qr_chats');
    const savedContacts = localStorage.getItem('whatsapp_qr_contacts');

    if (savedConnection === 'true') {
      setIsConnected(true);
      setConnectionStep('connected');
      loadSampleData();
    }

    if (savedMessages) setMessages(JSON.parse(savedMessages));
    if (savedChats) setChats(JSON.parse(savedChats));
    if (savedContacts) setContacts(JSON.parse(savedContacts));
  }, []);

  // Gerar QR Code simulado
  const generateQRCode = () => {
    setConnectionStep('generating');
    toast.loading('Gerando QR Code...', { id: 'qr-gen' });

    setTimeout(() => {
      // QR Code simulado (base64 de uma imagem QR real)
      const simulatedQR = `data:image/png;base64,`;

      setQrCode(simulatedQR);
      setConnectionStep('waiting');
      toast.success('QR Code gerado! Escaneie com seu celular', { id: 'qr-gen' });

      // Simular conex√£o ap√≥s 10 segundos
      setTimeout(() => {
        connectWhatsApp();
      }, 10000);
    }, 2000);
  };

  // Simular conex√£o do WhatsApp
  const connectWhatsApp = () => {
    setConnectionStep('connected');
    setIsConnected(true);
    setQrCode(null);
    localStorage.setItem('whatsapp_qr_connected', 'true');
    toast.success('WhatsApp conectado com sucesso!');
    loadSampleData();
  };

  // Carregar dados de exemplo
  const loadSampleData = () => {
    const sampleContacts: WhatsAppContact[] = [
      {
        id: '5511999999999',
        name: 'TechCorp Fornecedor',
        number: '+5511999999999',
        isMyContact: true
      },
      {
        id: '5511888888888',
        name: 'Eletr√¥nicos Brasil',
        number: '+5511888888888',
        isMyContact: true
      },
      {
        id: '5511777777777',
        name: 'Conecta Solu√ß√µes',
        number: '+5511777777777',
        isMyContact: true
      }
    ];

    const sampleChats: WhatsAppChat[] = sampleContacts.map(contact => ({
      id: contact.id,
      name: contact.name,
      isGroup: false,
      lastMessage: {
        body: 'Cota√ß√£o enviada conforme solicitado',
        timestamp: Date.now() - Math.random() * 86400000,
        fromMe: false
      },
      unreadCount: Math.floor(Math.random() * 3)
    }));

    setContacts(sampleContacts);
    setChats(sampleChats);
    setStats({
      totalMessages: 24,
      totalChats: sampleChats.length,
      totalContacts: sampleContacts.length,
      messagesReceived: 18
    });

    localStorage.setItem('whatsapp_qr_contacts', JSON.stringify(sampleContacts));
    localStorage.setItem('whatsapp_qr_chats', JSON.stringify(sampleChats));
  };

  // Desconectar WhatsApp
  const disconnectWhatsApp = () => {
    setIsConnected(false);
    setConnectionStep('disconnected');
    setQrCode(null);
    setMessages([]);
    setChats([]);
    setContacts([]);
    setSelectedChat(null);
    localStorage.removeItem('whatsapp_qr_connected');
    localStorage.removeItem('whatsapp_qr_messages');
    localStorage.removeItem('whatsapp_qr_chats');
    localStorage.removeItem('whatsapp_qr_contacts');
    toast.success('WhatsApp desconectado');
  };

  // Enviar mensagem
  const sendMessage = () => {
    if (!selectedChat || !messageText.trim() || !isConnected) return;

    const newMessage: WhatsAppMessage = {
      id: `msg_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      from: 'me',
      to: selectedChat.id,
      body: messageText,
      timestamp: Date.now(),
      fromMe: true,
      type: 'text'
    };

    const updatedMessages = [newMessage, ...messages];
    setMessages(updatedMessages);
    localStorage.setItem('whatsapp_qr_messages', JSON.stringify(updatedMessages));
    
    setMessageText('');
    toast.success('Mensagem enviada!');
  };

  // Simular recebimento de mensagem
  const simulateIncomingMessage = () => {
    if (!isConnected) {
      toast.error('Conecte o WhatsApp primeiro');
      return;
    }

    const sampleMessages = [
      {
        from: '+5511999999999',
        body: `Ol√°! Segue nossa cota√ß√£o:

PRODUTO: Cabo HDMI 2.0 - 2m
QUANTIDADE: 100 unidades
PRE√áO UNIT√ÅRIO: R$ 12,50
PRE√áO TOTAL: R$ 1.250,00
PRAZO: 5 dias √∫teis
PAGAMENTO: 30 dias

Aguardo retorno!`,
        contact: { name: 'TechCorp Fornecedor', number: '+5511999999999' }
      },
      {
        from: '+5511888888888',
        body: `Boa tarde! Cota√ß√£o solicitada:

- Mouse USB: 50 unidades x R$ 25,90 = R$ 1.295,00
- Teclado ABNT2: 30 unidades x R$ 45,00 = R$ 1.350,00

Total: R$ 2.645,00
Prazo: 7 dias
Condi√ß√µes: √Ä vista com 5% desconto`,
        contact: { name: 'Eletr√¥nicos Brasil', number: '+5511888888888' }
      }
    ];

    const randomMessage = sampleMessages[Math.floor(Math.random() * sampleMessages.length)];
    
    const newMessage: WhatsAppMessage = {
      id: `msg_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      from: randomMessage.from,
      to: 'me',
      body: randomMessage.body,
      timestamp: Date.now(),
      fromMe: false,
      type: 'text',
      contact: randomMessage.contact
    };

    const updatedMessages = [newMessage, ...messages];
    setMessages(updatedMessages);
    localStorage.setItem('whatsapp_qr_messages', JSON.stringify(updatedMessages));
    
    setStats(prev => ({
      ...prev,
      totalMessages: prev.totalMessages + 1,
      messagesReceived: prev.messagesReceived + 1
    }));

    toast.success(`Nova mensagem de ${randomMessage.contact.name}!`);
  };

  const formatTime = (timestamp: number) => {
    return new Date(timestamp).toLocaleString('pt-BR');
  };

  const tabs = [
    { id: 'qr' as const, label: 'QR Code', icon: QrCode },
    { id: 'dashboard' as const, label: 'Dashboard', icon: Monitor },
    { id: 'messages' as const, label: 'Mensagens', icon: MessageCircle },
    { id: 'contacts' as const, label: 'Contatos', icon: Users },
    { id: 'settings' as const, label: 'Configura√ß√µes', icon: Settings }
  ];

  return (
    <div className="max-w-7xl mx-auto space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="p-3 bg-green-100 rounded-lg">
              <MessageSquare className="w-8 h-8 text-green-600" />
            </div>
            <div>
              <h1 className="text-2xl font-bold text-gray-900">
                WhatsApp QR Code
              </h1>
              <p className="text-gray-600">
                Sistema n√£o oficial com QR Code - 100% frontend
              </p>
            </div>
          </div>
          
          <div className="flex items-center space-x-4">
            <div className={`flex items-center space-x-2 px-3 py-2 rounded-lg text-white ${
              isConnected ? 'bg-green-500' : 'bg-red-500'
            }`}>
              <div className="w-2 h-2 rounded-full bg-white animate-pulse" />
              <span className="text-sm font-medium">
                {isConnected ? 'Conectado' : 'Desconectado'}
              </span>
            </div>
            
            {isConnected && (
              <button
                onClick={simulateIncomingMessage}
                className="flex items-center space-x-2 px-3 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              >
                <Download className="w-4 h-4" />
                <span>Simular Mensagem</span>
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Navigation */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200">
        <div className="flex space-x-1 p-1">
          {tabs.map((tab) => {
            const Icon = tab.icon;
            return (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center space-x-2 px-4 py-3 rounded-lg text-sm font-medium transition-colors ${
                  activeTab === tab.id
                    ? 'bg-green-100 text-green-700'
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                }`}
              >
                <Icon className="w-4 h-4" />
                <span>{tab.label}</span>
              </button>
            );
          })}
        </div>
      </div>

      {/* Content */}
      <div className="space-y-6">
        {/* QR Code */}
        {activeTab === 'qr' && (
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-8">
            <div className="text-center">
              <h3 className="text-lg font-semibold text-gray-900 mb-6">
                Conectar WhatsApp
              </h3>
              
              {connectionStep === 'disconnected' && (
                <div className="space-y-6">
                  <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto">
                    <Smartphone className="w-8 h-8 text-gray-400" />
                  </div>
                  <div>
                    <h4 className="text-lg font-medium text-gray-900 mb-2">
                      Pronto para conectar
                    </h4>
                    <p className="text-gray-600 mb-6">
                      Clique no bot√£o abaixo para gerar o QR Code e conectar seu WhatsApp
                    </p>
                    <button
                      onClick={generateQRCode}
                      className="flex items-center space-x-2 px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors mx-auto"
                    >
                      <QrCode className="w-5 h-5" />
                      <span>Gerar QR Code</span>
                    </button>
                  </div>
                </div>
              )}

              {connectionStep === 'generating' && (
                <div className="space-y-4">
                  <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto">
                    <Zap className="w-8 h-8 text-blue-600 animate-pulse" />
                  </div>
                  <div>
                    <h4 className="text-lg font-medium text-gray-900">Gerando QR Code...</h4>
                    <p className="text-gray-600">Aguarde um momento</p>
                  </div>
                </div>
              )}

              {connectionStep === 'waiting' && qrCode && (
                <div className="space-y-6">
                  <div className="flex justify-center">
                    <img 
                      src={qrCode} 
                      alt="QR Code WhatsApp" 
                      className="w-64 h-64 border border-gray-300 rounded-lg"
                    />
                  </div>
                  <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                    <h4 className="font-medium text-blue-900 mb-2">üì± Como conectar:</h4>
                    <ol className="text-sm text-blue-800 space-y-1 list-decimal list-inside text-left max-w-md mx-auto">
                      <li>Abra o WhatsApp no seu celular</li>
                      <li>Toque em "Mais op√ß√µes" ou "Configura√ß√µes"</li>
                      <li>Selecione "Dispositivos conectados"</li>
                      <li>Toque em "Conectar um dispositivo"</li>
                      <li>Escaneie este QR Code</li>
                    </ol>
                  </div>
                  <div className="flex space-x-3 justify-center">
                    <button
                      onClick={generateQRCode}
                      className="flex items-center space-x-2 px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
                    >
                      <RefreshCw className="w-4 h-4" />
                      <span>Novo QR</span>
                    </button>
                    <button
                      onClick={connectWhatsApp}
                      className="flex items-center space-x-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                    >
                      <Wifi className="w-4 h-4" />
                      <span>Simular Conex√£o</span>
                    </button>
                  </div>
                </div>
              )}

              {connectionStep === 'connected' && (
                <div className="space-y-6">
                  <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto">
                    <CheckCircle className="w-8 h-8 text-green-600" />
                  </div>
                  <div>
                    <h4 className="text-lg font-medium text-green-900">WhatsApp Conectado!</h4>
                    <p className="text-green-700 mb-6">Seu WhatsApp est√° conectado e funcionando.</p>
                    <button
                      onClick={disconnectWhatsApp}
                      className="flex items-center space-x-2 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors mx-auto"
                    >
                      <WifiOff className="w-4 h-4" />
                      <span>Desconectar</span>
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Dashboard */}
        {activeTab === 'dashboard' && (
          <div className="space-y-6">
            {!isConnected ? (
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6 text-center">
                <AlertTriangle className="w-12 h-12 text-yellow-600 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-yellow-900 mb-2">Conecte o WhatsApp primeiro</h3>
                <p className="text-yellow-700 mb-4">V√° para a aba "QR Code" para conectar</p>
                <button
                  onClick={() => setActiveTab('qr')}
                  className="px-4 py-2 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700"
                >
                  Ir para QR Code
                </button>
              </div>
            ) : (
              <>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                  <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-gray-600">Total Mensagens</p>
                        <p className="text-2xl font-bold text-gray-900">{stats.totalMessages}</p>
                      </div>
                      <MessageCircle className="w-8 h-8 text-blue-600" />
                    </div>
                  </div>

                  <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-gray-600">Conversas Ativas</p>
                        <p className="text-2xl font-bold text-gray-900">{stats.totalChats}</p>
                      </div>
                      <MessageSquare className="w-8 h-8 text-green-600" />
                    </div>
                  </div>

                  <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-gray-600">Contatos</p>
                        <p className="text-2xl font-bold text-gray-900">{stats.totalContacts}</p>
                      </div>
                      <Users className="w-8 h-8 text-purple-600" />
                    </div>
                  </div>

                  <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-gray-600">Recebidas</p>
                        <p className="text-2xl font-bold text-gray-900">{stats.messagesReceived}</p>
                      </div>
                      <Download className="w-8 h-8 text-orange-600" />
                    </div>
                  </div>
                </div>

                {/* Mensagens Recentes */}
                <div className="bg-white rounded-lg shadow-sm border border-gray-200">
                  <div className="p-6 border-b border-gray-200">
                    <h3 className="text-lg font-semibold text-gray-900">Mensagens Recentes</h3>
                  </div>
                  <div className="divide-y divide-gray-200 max-h-96 overflow-y-auto">
                    {messages.slice(0, 10).map((message) => (
                      <div key={message.id} className="p-4">
                        <div className="flex items-start justify-between">
                          <div className="flex-1">
                            <div className="flex items-center space-x-2 mb-1">
                              <span className="font-medium text-gray-900">
                                {message.contact?.name || message.from}
                              </span>
                              {message.fromMe && (
                                <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                                  Enviada
                                </span>
                              )}
                            </div>
                            <p className="text-gray-700 text-sm whitespace-pre-wrap">{message.body}</p>
                          </div>
                          <span className="text-xs text-gray-500">
                            {formatTime(message.timestamp)}
                          </span>
                        </div>
                      </div>
                    ))}
                    {messages.length === 0 && (
                      <div className="p-8 text-center text-gray-500">
                        <MessageSquare className="w-12 h-12 text-gray-300 mx-auto mb-4" />
                        <p>Nenhuma mensagem ainda</p>
                        <button
                          onClick={simulateIncomingMessage}
                          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                        >
                          Simular Primeira Mensagem
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              </>
            )}
          </div>
        )}

        {/* Messages */}
        {activeTab === 'messages' && (
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Chats List */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200">
              <div className="p-4 border-b border-gray-200">
                <h3 className="text-lg font-semibold text-gray-900">Conversas</h3>
              </div>
              <div className="divide-y divide-gray-200 max-h-96 overflow-y-auto">
                {chats.map((chat) => (
                  <button
                    key={chat.id}
                    onClick={() => setSelectedChat(chat)}
                    className={`w-full p-4 text-left hover:bg-gray-50 transition-colors ${
                      selectedChat?.id === chat.id ? 'bg-green-50 border-r-2 border-green-500' : ''
                    }`}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex-1 min-w-0">
                        <p className="font-medium text-gray-900 truncate">{chat.name}</p>
                        {chat.lastMessage && (
                          <p className="text-sm text-gray-600 truncate">
                            {chat.lastMessage.body}
                          </p>
                        )}
                      </div>
                      {chat.unreadCount > 0 && (
                        <div className="bg-green-600 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                          {chat.unreadCount}
                        </div>
                      )}
                    </div>
                  </button>
                ))}
                {chats.length === 0 && (
                  <div className="p-8 text-center text-gray-500">
                    {isConnected ? 'Nenhuma conversa ainda' : 'Conecte o WhatsApp primeiro'}
                  </div>
                )}
              </div>
            </div>

            {/* Chat Area */}
            <div className="lg:col-span-2 bg-white rounded-lg shadow-sm border border-gray-200">
              {selectedChat ? (
                <>
                  <div className="p-4 border-b border-gray-200">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-green-100 rounded-full flex items-center justify-center">
                        <Phone className="w-5 h-5 text-green-600" />
                      </div>
                      <div>
                        <p className="font-medium text-gray-900">{selectedChat.name}</p>
                        <p className="text-sm text-gray-600">
                          {selectedChat.isGroup ? 'Grupo' : 'Contato'}
                        </p>
                      </div>
                    </div>
                  </div>

                  <div className="h-96 p-4 overflow-y-auto">
                    <div className="space-y-4">
                      {messages
                        .filter(msg => msg.from === selectedChat.id || msg.to === selectedChat.id)
                        .slice(0, 20)
                        .reverse()
                        .map((message) => (
                          <div
                            key={message.id}
                            className={`flex ${message.fromMe ? 'justify-end' : 'justify-start'}`}
                          >
                            <div
                              className={`max-w-xs px-4 py-2 rounded-lg ${
                                message.fromMe
                                  ? 'bg-green-600 text-white'
                                  : 'bg-gray-100 text-gray-900'
                              }`}
                            >
                              <p className="whitespace-pre-wrap">{message.body}</p>
                              <p className={`text-xs mt-1 ${
                                message.fromMe ? 'text-green-100' : 'text-gray-500'
                              }`}>
                                {formatTime(message.timestamp)}
                              </p>
                            </div>
                          </div>
                        ))}
                    </div>
                  </div>

                  <div className="p-4 border-t border-gray-200">
                    <div className="flex space-x-3">
                      <textarea
                        value={messageText}
                        onChange={(e) => setMessageText(e.target.value)}
                        placeholder="Digite sua mensagem..."
                        className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent resize-none"
                        rows={2}
                        onKeyPress={(e) => {
                          if (e.key === 'Enter' && !e.shiftKey) {
                            e.preventDefault();
                            sendMessage();
                          }
                        }}
                        disabled={!isConnected}
                      />
                      <button
                        onClick={sendMessage}
                        disabled={!messageText.trim() || !isConnected}
                        className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors disabled:opacity-50"
                      >
                        <Send className="w-4 h-4" />
                      </button>
                    </div>
                  </div>
                </>
              ) : (
                <div className="h-full flex items-center justify-center">
                  <div className="text-center">
                    <MessageCircle className="w-12 h-12 text-gray-300 mx-auto mb-4" />
                    <p className="text-gray-500">Selecione uma conversa para come√ßar</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Contacts */}
        {activeTab === 'contacts' && (
          <div className="bg-white rounded-lg shadow-sm border border-gray-200">
            <div className="p-6 border-b border-gray-200">
              <h3 className="text-lg font-semibold text-gray-900">
                Contatos ({contacts.length})
              </h3>
            </div>
            <div className="divide-y divide-gray-200 max-h-96 overflow-y-auto">
              {contacts.map((contact) => (
                <div key={contact.id} className="p-4">
                  <div className="flex items-center space-x-3">
                    <div className="w-10 h-10 bg-gray-100 rounded-full flex items-center justify-center">
                      <Users className="w-5 h-5 text-gray-600" />
                    </div>
                    <div className="flex-1">
                      <p className="font-medium text-gray-900">{contact.name}</p>
                      <p className="text-sm text-gray-600">{contact.number}</p>
                    </div>
                    {contact.isMyContact && (
                      <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">
                        Contato
                      </span>
                    )}
                  </div>
                </div>
              ))}
              {contacts.length === 0 && (
                <div className="p-8 text-center text-gray-500">
                  {isConnected ? 'Nenhum contato ainda' : 'Conecte o WhatsApp primeiro'}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Settings */}
        {activeTab === 'settings' && (
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-6">Configura√ß√µes</h3>
            
            <div className="space-y-6">
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                <h4 className="font-medium text-blue-900 mb-2">‚ÑπÔ∏è Como Funciona</h4>
                <ul className="text-sm text-blue-800 space-y-1">
                  <li>‚Ä¢ Sistema simula WhatsApp Web n√£o oficial</li>
                  <li>‚Ä¢ QR Code √© gerado automaticamente</li>
                  <li>‚Ä¢ Conex√£o √© simulada para demonstra√ß√£o</li>
                  <li>‚Ä¢ Dados ficam salvos no navegador</li>
                  <li>‚Ä¢ Funciona 100% offline</li>
                </ul>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="space-y-4">
                  <h4 className="font-medium text-gray-900">Status da Conex√£o</h4>
                  <div className="space-y-2">
                    <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <span>WhatsApp</span>
                      <span className={`px-2 py-1 text-xs rounded-full ${
                        isConnected ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                      }`}>
                        {isConnected ? 'Conectado' : 'Desconectado'}
                      </span>
                    </div>
                    <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <span>Sess√£o</span>
                      <span className={`px-2 py-1 text-xs rounded-full ${
                        connectionStep !== 'disconnected' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                      }`}>
                        {connectionStep !== 'disconnected' ? 'Ativa' : 'Inativa'}
                      </span>
                    </div>
                  </div>
                </div>

                <div className="space-y-4">
                  <h4 className="font-medium text-gray-900">A√ß√µes</h4>
                  <div className="space-y-2">
                    {!isConnected ? (
                      <button
                        onClick={() => setActiveTab('qr')}
                        className="w-full flex items-center justify-center space-x-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                      >
                        <QrCode className="w-4 h-4" />
                        <span>Conectar WhatsApp</span>
                      </button>
                    ) : (
                      <button
                        onClick={disconnectWhatsApp}
                        className="w-full flex items-center justify-center space-x-2 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
                      >
                        <WifiOff className="w-4 h-4" />
                        <span>Desconectar</span>
                      </button>
                    )}
                    
                    {isConnected && (
                      <button
                        onClick={simulateIncomingMessage}
                        className="w-full flex items-center justify-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                      >
                        <Download className="w-4 h-4" />
                        <span>Simular Mensagem</span>
                      </button>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};